// Sports - Balanced filter (aim for 20-30% pass-through)
const articles = $input.all();

// BLACKLIST - Remove noise
const noisePatterns = [
  // Speculation & analysis
  /\brumou?rs?\b/i,
  /\bspeculation\b/i,
  /could (sign|join|leave|move)/i,
  /might (sign|join|leave)/i,
  /linked with/i,
  /interested in/i,
  /eyes? (move|transfer)/i,
  /set to (sign|join)/i,
  /reportedly/i,

  // Pre-match & analysis
  /preview:/i,
  /\bprediction/i,
  /\bpredicts?\b/i,
  /ahead of/i,
  /prepares? for/i,
  /ready for/i,
  /vs\b.*(preview|prediction)/i,

  // Training & press
  /press conference/i,
  /training (camp|session|ground)/i,
  /\bpractice\b/i,
  /weigh[- ]?in/i,
  /face[- ]?off/i,
  /staredown/i,
  /callout/i,
  /calls? out/i,
  /\bslams?\b/i,
