  // Minor injuries & updates
  /minor (injury|knock|problem)/i,
  /doubt(ful)? for/i,
  /assessed/i,
  /\bupdate:/i,
  /injury (update|concern)/i,

  // Social media drama
  /\btweets?\b/i,
  /\bposts?\b/i,
  /social media/i,
  /instagram/i
];

// WHITELIST - Major events with specific indicators
const f1Patterns = [
  // Race results - must have outcome
  /\b(wins?|won|victory|takes|claims|secures).*(grand prix|gp|race)\b/i,
  /\b(verstappen|hamilton|leclerc|norris|alonso|sainz).*(wins?|won|victory|pole)/i,
  /\b(red bull|ferrari|mercedes|mclaren).*(wins?|won|victory|dominant)/i,
  /\bpodium:?\s*[0-9]/i,  // "Podium: 1. Verstappen"
  /\b(1st|2nd|3rd|first|second|third|p1|p2|p3)\s*(place|position|finish)/i,

  // Championship
  /\b(wins?|clinches?|secures?|claims?).*(championship|title|world champion)/i,
  /\bchampionship.*(decided|wrapped up|sealed)/i,

  // Transfers - must have confirmation or money
  /\b(signs|signed|joins|joined).*(for|with|at).*(red bull|ferrari|mercedes|mclaren)/i,
  /\b(red bull|ferrari|mercedes|mclaren).*(signs|confirms?|announces?)/i,
  /â‚¬[0-9]+.*million/i,
  /multi[- ]?year (deal|contract)/i,

  // Major incidents
  /\bcrash.*(hospital|injury|serious|ruled out)/i,
  /\b(retires?|retirement|final (season|race|grand prix))\b/i,
  /\b(dies|died|death|fatal|killed)\b/i
];

const bayernPatterns = [
  // Match results - must have score or outcome
  /\bbayern\b.*\b[0-9]+[-â€“][0-9]+\b/i,  // Bayern 3-1
  /\b[0-9]+[-â€“][0-9]+\b.*\bbayern\b/i,  // 3-1 Bayern
  /\bbayern\b.*(wins?|won|beat|beats|defeats?|defeated|loses?|lost|draws?|drew)/i,
  /(wins?|won|beat|beats|defeats?|defeated|loses?|lost).*(to|against)?\s*\bbayern\b/i,

  // Champions League - knockout stages only
  /\bbayern\b.*(champions league|ucl).*(quarter|semi|final|knockout|eliminated|through)/i,
  /(champions league|ucl).*(quarter|semi|final|knockout).*\bbayern\b/i,

  // Musiala - goals/assists in big matches
  /\bmusiala\b.*(scores?|scored|goal|goals|assist|assists|brace|hat[- ]?trick)/i,
  /\b(goal|goals|brace).*(musiala|jamal)/i,

  // Transfers - must have confirmation or money
  /\bbayern\b.*(signs|signed|confirms?|announces?).*(transfer|signing)/i,
  /(signs|joins|leaves|transfer).*(for|with|from)?\s*\bbayern\b/i,
  /â‚¬[0-9]{2,}.*million.*\bbayern\b/i,
  /\bbayern\b.*â‚¬[0-9]{2,}.*million/i,

  // Manager changes
  /(manager|coach).*(fired|sacked|appointed|hired|resigns?|leaves?).*\bbayern\b/i,
  /\bbayern\b.*(fires?|sacks?|appoints?|hires?).*(manager|coach)/i,

  // Major incidents
  /\bmusiala\b.*(injury|injured|out for|sidelined)/i,
  /\bbayern\b.*(crisis|emergency|disaster)/i
];

const ufcPatterns = [
  // Fight results - must have outcome
  /\b(defeats?|defeated|beats?|beat|knocked out|submitted|dominates?).*(via|by|in).*(ko|tko|submission|decision)/i,
  /\b(ko|tko|knockout|submission|decision).*(victory|win)/i,
  /\b(wins?|won|victory|victorious).*(via|by|in).*(ko|tko|submission|decision)/i,

  // Specific fighters - only results or major announcements
  /\b(tsarukyan|arman)\b.*(wins?|won|beats?|beat|defeats?|defeated|loses?|lost|scheduled|signed|announced)/i,
  /\b(topuria|ilia)\b.*(wins?|won|beats?|beat|defeats?|defeated|loses?|lost|scheduled|signed|announced)/i,

  // Title fights
  /\btitle fight\b.*(result|winner|new champion)/i,
  /\b(wins?|defends?|loses?).*(belt|title|championship)/i,
  /\bnew.*champion\b/i,
  /\bchampionship.*(bout|fight).*(result|winner|scheduled)/i,

  // Main events - only with dates/confirmed
  /\bmain event\b.*(ufc [0-9]{3}|scheduled|confirmed|announced)/i,
  /\bufc [0-9]{3}\b.*(headline|main event|title fight)/i,

  // Major announcements
  /\b(retires?|retirement|final fight)\b/i,
  /\binjury.*(out|withdraw|replaced|sidelined)/i,
  /\bstripped of.*title\b/i
];

const filtered = articles.filter(item => {
  const title = (item.json.title || '');
  const desc = (item.json.description || '');
  const combined = title + ' ' + desc;
  const source = (item.json.source || '').toLowerCase();

  // ====================================================================
  // VIP FIGHTERS/PLAYERS - Always pass, bypass ALL filters
  // Add new VIPs below using the same pattern format
  // ====================================================================
  const vipFighters = [
    /\b(arman|tsarukyan)\b/i,        // UFC: Arman Tsarukyan
    /\b(ilia|topuria)\b/i,           // UFC: Ilia Topuria
    /\b(musiala|jamal)\b/i,          // Bayern: Jamal Musiala
    // Add more here, for example:
    // /\b(verstappen|max)\b/i,      // F1: Max Verstappen
    // /\b(kane|harry)\b/i,          // Bayern: Harry Kane
  ];

  const isVIP = vipFighters.some(pattern => pattern.test(combined));
  if (isVIP) return true;  // Skip all other checks for VIPs
  // ====================================================================

  // First, remove noise
  const isNoise = noisePatterns.some(pattern => pattern.test(combined));
  if (isNoise) return false;

  // Determine which patterns to use
  let patterns = [];

  if (source.includes('formula') || source.includes('f1') || source.includes('motorsport')) {
    patterns = f1Patterns;
  } else if (source.includes('bayern') || source.includes('bundesliga') || source.includes('football') || source.includes('soccer')) {
    patterns = bayernPatterns;
  } else if (source.includes('ufc') || source.includes('mma')) {
    patterns = ufcPatterns;
  } else {
    // Unknown source - try all patterns
    patterns = [...f1Patterns, ...bayernPatterns, ...ufcPatterns];
  }

  // Check if matches major event patterns
  return patterns.some(pattern => pattern.test(combined));
});

