// Global deduplication, 24h filter, and limit per section
const articles = $input.all();
const now = Date.now();
const cutoff24h = now - (48 * 60 * 60 * 1000);

// Filter to last 24 hours
const recent = articles.filter(a => {
  const articleTime = a.json.isoDate || 0;
  return articleTime >= cutoff24h;
});

// Deduplicate by title
const seen = new Map();
const deduped = [];
for (const article of recent) {
  const title = (article.json.title || '').toLowerCase().trim();
  const key = title.substring(0, 50).replace(/[^a-z0-9]/g, '');

  if (!seen.has(key)) {
    seen.set(key, true);
    deduped.push(article);
  }
}

// Sort by date (newest first)
deduped.sort((a, b) => (b.json.isoDate || 0) - (a.json.isoDate || 0));

// Limit to 5 per section
const sections = { sports: [], tech: [], world: [] };
for (const article of deduped) {
  const section = article.json.section || 'other';
  if (sections[section] && sections[section].length < 5) {
    sections[section].push(article);
  }
}

// Flatten back to array
const result = [
  ...sections.tech,
  ...sections.sports,
  ...sections.world
];

return result;

