// Tech - Balanced filter (aim for 20-30% pass-through)
const articles = $input.all();

// BLACKLIST - Remove noise
const noisePatterns = [
  // Opinion & analysis
  /^opinion:/i,
  /^commentary:/i,
  /^analysis:/i,
  /\bopinion\b.*\bpiece\b/i,

  // Hype & speculation
  /\bwill change\b/i,
  /\bcould (transform|revolutionize|disrupt)/i,
  /\bmight (be|become)/i,
  /\bpredicts?\b/i,
  /\bprediction/i,
  /\brumou?rs?\b/i,
  /reportedly/i,
  /sources say/i,

  // Generic AI content
  /\bhow (to use|ai can|to leverage)\b/i,
  /\bguide to\b/i,
  /\btips for\b/i,
  /\bbest.*ai tools\b/i,
  /\bai.*future\b/i,

  // Minor updates
  /beta (version|release|launch)/i,
  /minor (update|fix|patch|bug)/i,
  /bug fix/i,
  /\bpatch notes\b/i,

  // Conferences without announcements
  /\b(speaking|presents?|presenting) at\b/i,
  /conference appearance/i,
  /panel discussion/i,

  // Funding (unless massive)
  /raises \$[0-9]{1,2}M/i,  // Under $100M
  /series [a-c] (funding|round)/i,
  /seed (funding|round)/i
];

// WHITELIST - Major events with specific indicators
const aiPatterns = [
  // Major model releases - must have version/name
  /\b(gpt-?[0-9o]|claude [0-9]|gemini [0-9])/i,
  /\b(openai|anthropic|google).*(releases?|launches?|unveils?|announces?).*(model|gpt|claude|gemini)/i,
  /\bnew.*(gpt|claude|gemini|llama).*(model|version|release)/i,
  /\b(chatgpt|claude|gemini|bard).*(upgrade|major update)/i,

  // Major features (not minor updates)
  /\b(openai|anthropic|google).*(adds?|launches?|introduces?).*\b(feature|capability|tool)/i,

  // Breakthroughs
  /\bbreakthrough\b/i,
  /state[- ]of[- ]the[- ]art/i,
  /\b(achieves?|achieved|surpasses?|beats?).*(human|benchmark|record)/i,
  /\bagi\b/i,

  // People
  /\b(sam altman|dario amodei|demis hassabis|jensen huang|yann lecun|geoffrey hinton)/i
];

const robotPatterns = [
  // Humanoid robots - deployment/major milestones only
  /\bhumanoid.*(robot|deployed|production|commercial|factory|ships?)/i,
  /\b(optimus|figure|atlas|digit).*(deployed|production|ships?|sells?|commercial)/i,
  /\brobot.*(production|factory|deployed|ships?|delivered)/i,
  /\b(tesla|boston dynamics|figure ai).*(robot|humanoid).*(unveiled|launches?|ships?)/i
];

const regulationPatterns = [
  // Major regulations - passed or proposed by major bodies
  /\b(ai|tech).*(regulation|act|law|bill).*(passed|signed|approved|enacted)/i,
  /\b(eu|congress|senate|parliament).*(passes?|approves?|bans?).*(ai|tech)/i,
  /\bai (act|law|regulation|bill)\b/i,
  /\bexecutive order.*(ai|tech)/i,
  /\b(ftc|doj|sec|fcc).*(sues?|fines?|blocks?|bans?).*(openai|google|meta|microsoft|amazon|nvidia)/i,

  // Crypto - only major regulatory moves
  /\b(bitcoin|crypto|ethereum).*(regulation|ban|law|approved|etf)/i,
  /\bsec.*(approves?|denies?|rejects?).*(bitcoin|crypto|etf)/i
];

const securityPatterns = [
  // Major breaches only - with scale indicators
  /\bbreach.*(million|billion|massive|major|critical)/i,
  /\b(million|billion).*(users?|accounts?|records?).*(breach|leaked|stolen|compromised)/i,
  /\b(hack|breach).*(openai|anthropic|google|microsoft|amazon|meta|apple)/i,
  /\bransomware.*(attack|demands?).*(million|major|critical)/i,
  /\bzero[- ]?day.*(exploit|vulnerability).*(critical|widespread)/i,
  /\bcritical.*(vulnerability|flaw).*(openai|google|microsoft|apple|meta)/i
];

const bigTechPatterns = [
  // Acquisitions - $100M+ or major companies
  /\b(acquisition|acquires?|buys?).*(billion|\$[0-9]{3,}M)/i,
  /\b(openai|anthropic|google|microsoft|nvidia|meta|apple).*(acquires?|buys?)/i,

  // Major company moves
  /\b(openai|anthropic|google|microsoft|nvidia|meta|apple).*(launches?|unveils?|announces?).*(product|service)/i,
  /\b(nvidia|amd|intel).*(chip|gpu|processor).*(launches?|announces?|unveils?)/i,

  // Leadership changes
  /\b(ceo|founder|chief).*(fired|resigns?|steps down|appointed|hired).*(openai|anthropic|google|microsoft|nvidia)/i,
  /\b(openai|anthropic|google|microsoft|nvidia).*(ceo|founder|chief).*(fired|resigns?|appointed)/i,

  // Layoffs - major only
  /\b(layoffs?|fires?|cuts?).*(thousand|[0-9]{4,}|[0-9]{1,3}k)/i,

  // Shutdowns
  /\b(shuts? down|discontinues?|kills|ends).*(product|service)/i
];

const allPatterns = [
  ...aiPatterns,
  ...robotPatterns,
  ...regulationPatterns,
  ...securityPatterns,
  ...bigTechPatterns
];

const filtered = articles.filter(item => {
  const title = (item.json.title || '');
  const desc = (item.json.description || '');
  const combined = title + ' ' + desc;
  const source = (item.json.source || '').toLowerCase();

  // Trusted sources - pass most things (but still filter noise)
  if (source.includes('openai') || 
      source.includes('anthropic') || 
      source.includes('deepmind')) {
    const isNoise = noisePatterns.some(pattern => pattern.test(combined));
    return !isNoise;
  }

  // CVE - always pass
  if (source.includes('cve')) {
    return true;
  }

  // Others - filter noise then check patterns
  const isNoise = noisePatterns.some(pattern => pattern.test(combined));
  if (isNoise) return false;

  return allPatterns.some(pattern => pattern.test(combined));
});

return filtered;

