  /\b(ship|ferry|plane|aircraft).*(sinks?|sank|crash|crashes|crashed).*(kills?|killed|dead) [0-9]{2,}/i,
  /\b[0-9]{2,}.*(dead|killed).*(ship|ferry|plane|aircraft|crash)/i
];

const filtered = articles.filter(item => {
  const title = (item.json.title || '');
  const desc = (item.json.description || '');
  const combined = title + ' ' + desc;

  // First, remove noise
  const isNoise = noisePatterns.some(pattern => pattern.test(combined));
  if (isNoise) return false;

  // Then check if matches major event patterns
  return majorEventPatterns.some(pattern => pattern.test(combined));
});

return filtered;

