// Parse Tech AI scores and filter articles
const input = $input.first().json;
const aiResponse = input.output || '';
const originalArticles = $('Prepare Tech for AI Scoring').first().json.originalArticles;

let scores = [];
try {
  // Extract JSON from response
  const jsonMatch = aiResponse.match(/\[.*\]/s);
  if (jsonMatch) {
    scores = JSON.parse(jsonMatch[0]);
  }
} catch (e) {
  // If parsing fails, return empty
  return [];
}

// Filter articles with score >= 70
const filtered = [];
for (const scoreObj of scores) {
  const idx = scoreObj.id - 1; // Convert to 0-based index
  if (idx >= 0 && idx < originalArticles.length && scoreObj.score >= 70) {
    filtered.push({
      json: {
        ...originalArticles[idx],
        ai_score: scoreObj.score
      }
    });
  }
}

return filtered;

